import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';

import Header from '../components/Header'

import Home from '../components/Home';
import Login from '../components/Login';
import Account from '../components/Account';

// import * as serviceWorker from './serviceWorker';
import { BrowserRouter as Router, Route } from 'react-router-dom';

class App extends Component {

    // constructor() {
    //     super();
    //     this.state = {
    //         user_id: null,
    //         user: null
    //     }
    // }

    // getCurrentUser = (userInfo) => {
    //     console.log(userInfo.user.id)
    //     this.setState({
    //         user_id: userInfo.user.id,
    //         user: userInfo.user
    //     }, () => {console.log(this.state.user_id)})
    // }

    // getUserToken = () => {
    //     return localStorage.getItem('jwt');
    // }

    state = {
        user: null,
        username: null
    }

    constructor() {
        super()
        this.username = React.createRef()
        this.password = React.createRef()

        if (this.getToken()) {
            this.getProfile()
        }

        this.logout = this.logout.bind(this)
    }

    login = (ev) => {
        ev.preventDefault()
        console.log('log in')
        console.log(ev.target.id)
        let username = this.username.current.value
        let password = this.password.current.value

        fetch('http://localhost:3000/api/v1/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ user: { username, password } })
        })
            .then(res => res.json())
            .then(json => {
                console.log('login:', json)
                if (json && json.jwt) {
                    this.saveToken(json.jwt)
                    this.getProfile()
                }
            })
            .then(() => {
                this.props.history.push('/')
            })
    }

    logout() {
        this.clearToken();
        this.setState({ user: null })
    }

    getProfile = () => {
        let token = this.getToken()
        fetch('http://localhost:3000/api/v1/profile', {
            headers: {
                'Authorization': 'Bearer ' + token
            }
        })
            .then(res => res.json())
            .then(json => {
                console.log('profile:', json)
                this.setState({ user: json.user })
            })
    }

    saveToken(jwt) {
        localStorage.setItem('jwt', jwt)
    }

    clearToken(jwt) {
        localStorage.setItem('jwt', '')
    }

    getToken(jwt) {
        return localStorage.getItem('jwt')
    }

    handleCreate = (newUser) => {
        fetch('http://localhost:3000/api/v1/users', {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ user: { ...newUser } })
        })
            .then(resp => resp.json())
            .then(json => { this.setState({ user: json.user }) })

    }

    render() {

        return(
            <Router>
                <React.Fragment>
                    <Header />
                    <Route exact path="/" render={routerProps => <Home {...routerProps} currentUserToken={this.getUserToken}/>} />
                    <Route exact path="/login" render={routerProps => <Login {...routerProps} onLogin={this.login}/>} />
                    <Route exact path="/account" component={Account} />
                </React.Fragment>
            </Router>

        );
    }
}

export default App;
